import React, { Component } from 'react';
import {
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle,
  Button,
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from 'reactstrap';
import Particles from 'react-particles-js';
import SkillBar from 'react-skillbars';
import birds from './imgs/delete.svg'
import { binary, mGlass, computer, element, fts, map } from './imgs'
import * as Scroll from 'react-scroll';
import { Link, Element, Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll'
import { Parallax, Background } from 'react-parallax';

import './App.css';
import 'bootstrap/dist/css/bootstrap.css';

const skills = [
  {
    "type": "JavaScript",
    "level": 85,
    "color": {
      "bar": "#3498db",
      "title": {
        "text": "#fff",
        "background": "#2980b9"
      }
    }
  },
  {
    "type": "React",
    "level": 80,
    "color": {
      "bar": "#4288d0",
      "title": {
        "text": "#fff",
        "background": "#124e8c"
      }
    }
  },
  {
    "type": "Angular",
    "level": 65,
    "color": {
      "bar": "#2c3e50",
      "title": {
        "text": "#fff",
        "background": "#2c3e50"
      }
    }
  },
  {
    "type": "HTML",
    "level": 75,
    "color": {
      "bar": "#5a68a5",
      "title": {
        "text": "#fff",
        "background": "#46465e"
      }
    }
  },
  {
    "type": "NodeJS",
    "level": 65,
    "color": {
      "bar": "#525252",
      "title": {
        "text": "#fff",
        "background": "#333333"
      }
    }
  },
  {
    "type": "MongoDB",
    "level": 65,
    "color": {
      "bar": "black",
      "title": {
        "text": "#111",
        "background": "#fff"
      }
    }
  },
  {
    "type": "Bootstrap",
    "level": 90,
    "color": {
      "bar": "#2ecc71",
      "title": {
        "text": "#fff",
        "background": "#27ae60"
      }
    }
  }
]

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
    };
  }
  render() {
    return (
      <div className="container-fluid" style={{ padding: "0px", }}>
        <Navbar color="light" light expand="md" fixed-top>
          <NavbarBrand href="/"><span style={{ color: "rgba(220,53,69)" }}>Clay</span> Palumbo</NavbarBrand>
          <NavbarToggler onClick={this.toggle} />
          <Collapse isOpen={this.state.isOpen} navbar>
            <Nav className="ml-auto" navbar>
              {/* <NavItem>
                <NavLink href="#">About</NavLink>
              </NavItem> */}
              <NavItem>
                <Link activeClass="active" to="normal" spy={true} smooth={true} duration={750} style={{ color: "rgba(0, 0, 0, 0.5)" }}>About</Link>
              </NavItem>
              <NavItem>
                <NavLink href="#">Applications</NavLink>
              </NavItem>
              <UncontrolledDropdown nav inNavbar>
                <DropdownToggle nav caret>
                  Social Pages
                </DropdownToggle>
                <DropdownMenu right>
                  <DropdownItem>
                    <NavLink href="https://github.com/kelptape" target="_blank">Github</NavLink>
                  </DropdownItem>
                  <DropdownItem>
                    <NavLink href="https://www.linkedin.com/in/clay-palumbo/" target="_blank">LinkedIn</NavLink>
                  </DropdownItem>
                </DropdownMenu>
              </UncontrolledDropdown>
            </Nav>
          </Collapse>
        </Navbar>
        <div className="App" >
          <div class="title-box bounceInLeft pb-3 pl-5 pr-5 pt-4">
            <h1> UI / UX Engineer</h1>
            <p class="text-white">(click the image)</p>
          </div>
          <div class="nameTitle bounceInLeft pb-3 pl-5 pr-5 pt-4">
            <p class="text-white"><span style={{}}>Think</span></p>
            <p class="text-white"><span style={{}}>Design</span></p>
            <p class="text-white"><span style={{}}>Engineer</span></p>
            <p class="text-white"><span style={{}}>Execute</span></p>

          </div>
          <Particles
            params={{
              "particles": {
                "number": {
                  "value": 100
                },
                "size": {
                  "value": 3
                },
              },
              "color": {
                "value": "black"
              },
              "interactivity": {
                "events": {
                  "onhover": {
                    "enable": true,
                    "mode": "repulse"
                  },
                  "onclick": {
                    "enable": true,
                    "mode": "push"
                  }
                }
              }

            }}
            style={{
              width: '100%',
              height: '100vh',
              background: `linear-gradient(rgba(38, 38, 38, 0.5), rgba(38, 38, 38, 0.7)), url(${fts})`,
              backgroundRepeat: "no-repeat",
              backgroundSize: "cover",
              backgroundPosition: "center",
            }}
          />
          <Element name="normal" className="element big"></Element>

          <div className="row text-center" style={{ margin: "20px" }}>
            <div className="col-1">
            </div>
            <Card className="col-3" style={{ margin: "20px", padding: "20px", border: "none" }}>
              <CardImg top width="10%" src={element} alt="Card image cap" />
              <CardBody>
                <CardTitle style={{ color: "#dc3545", fontSize: "24px" }}>Innovative Engineer</CardTitle>
                <CardText>Offering experience in the full SDLC from concept to production in developing modern applications and customizable solutions.</CardText>
              </CardBody>
            </Card>
            <Card className="col-3" style={{ margin: "20px", padding: "20px", border: "none" }}>
              <CardImg top width="90%" src={computer} alt="Card image cap" style={{}} />
              <CardBody>
                <CardTitle style={{ color: "#dc3545", fontSize: "24px" }}>Design Centric</CardTitle>
                <CardText>Able to set standards for design patterns through well- researched, cost-effective and responsive solutions.</CardText>
              </CardBody>
            </Card>
            <Card className="col-3" style={{ margin: "20px", padding: "20px", border: "none" }}>
              <CardImg top width="10%" src={mGlass} alt="Card image cap" />
              <CardBody>
                <CardTitle style={{ color: "#dc3545", fontSize: "24px" }}>Innovative Engineer</CardTitle>
                <CardText>Offering experience in the full software development lifecycle.</CardText>
              </CardBody>
            </Card>

          </div>
          <Parallax
            bgImage={map}
            strength={400}
            renderLayer={percentage => (
                <div
                    style={{
                        position: 'absolute',
                        background: `linear-gradient(rgba(88, 179, 122, ${percentage * 1}), rgba(38, 38, 38, ${percentage * 1}))`,
                        left: '0%',
                        top: '0%',
                        width: '100%',
                        height: percentage * 3000,
                    }}
                />
            )}
        >
        <div style={{
          height: "60vh",
          }}>
            <p>... Content</p>
            </div>
        </Parallax>      
        <div className="row">
            <div className="col-6">
            </div>
            <div className="col-6 p-5">
              <SkillBar
                skills={skills}
                animationDelay={0}
              />
            </div>
          </div>
          <div
            style={{
              background: `linear-gradient(rgba(88, 179, 122, 0.5), rgba(38, 38, 38, 0.7)), url(${fts})`,
              height: "70vh",
              backgroundRepeat: "no-repeat",
              backgroundSize: "cover",
              backgroundPosition: "center",
            }}
          >
          </div>
        </div>
      </div>
    );
  }
}


export default App;
